// Format weekly report summary (no email, just console log)
const data = $input.item.json;
const summary = data.summary;
const details = data.details;

const subject = `ðŸ“Š Weekly Material Delivery Report - ${data.report_date}`;

const textSummary = `
${'='.repeat(60)}
WEEKLY MATERIAL DELIVERY REPORT
${data.report_date}
${'='.repeat(60)}

OVERVIEW:
- Total Materials: ${summary.materials.total}
- Purchase Orders: ${summary.purchase_orders.total}
- Payment Completion: ${summary.payments.completion_percentage}%
- Total Deliveries: ${summary.deliveries.total}

ALERTS:
${summary.deliveries.delayed_count > 0 ? `ðŸš¨ ${summary.deliveries.delayed_count} Delayed Deliveries` : 'âœ… No delayed deliveries'}
${summary.purchase_orders.pending_release > 0 ? `âš ï¸ ${summary.purchase_orders.pending_release} Pending PO Releases` : ''}

UPCOMING DELIVERIES (Next 7 Days): ${details.upcoming_deliveries.length}
${details.upcoming_deliveries.slice(0, 5).map(d => `  - ${d.expected_delivery_date}: ${d.po_ref || 'N/A'} (${d.delivery_status})`).join('\n')}

PAYMENT SUMMARY:
- Total Amount: ${summary.payments.total_amount.toFixed(2)} AED
- Total Paid: ${summary.payments.total_paid.toFixed(2)} AED
- Outstanding: ${(summary.payments.total_amount - summary.payments.total_paid).toFixed(2)} AED
- Completion: ${summary.payments.completion_percentage}%

ACTIVITY THIS WEEK:
- New POs: ${summary.recent_activity.new_pos_last_week}
- Deliveries Completed: ${summary.recent_activity.deliveries_last_week}

${'='.repeat(60)}
`;

// Log to console
console.log(textSummary);

return {
  subject,
  summary_text: textSummary,
  report_date: data.report_date,
  delayed_count: summary.deliveries.delayed_count,
  pending_pos: summary.purchase_orders.pending_release,
  payment_completion: summary.payments.completion_percentage,
  upcoming_deliveries: details.upcoming_deliveries.length
};
